import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { 
  HomeIcon,
  ChartBarIcon,
  CurrencyDollarIcon,
  FolderIcon,
  Cog6ToothIcon,
  UserGroupIcon,
  DocumentTextIcon,
  BoltIcon,
  GlobeAltIcon,
  UserCircleIcon
} from '@heroicons/react/24/outline';
import './NavBar.css';

const NavBar = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    {
      id: 'home',
      label: 'لوحة التحكم',
      icon: <HomeIcon className="nav-icon" />,
      path: '/dashboard'
    },
    {
      id: 'reports',
      label: 'التقارير',
      icon: <DocumentTextIcon className="nav-icon" />,
      path: '/reports'
    },
    {
      id: 'templates',
      label: 'القوالب',
      icon: <FolderIcon className="nav-icon" />,
      path: '/templates'
    },
    {
      id: 'invoices',
      label: 'المخزون',
      icon: <ChartBarIcon className="nav-icon" />,
      path: '/invoices'
    },
    {
      id: 'customers',
      label: 'العملاء',
      icon: <UserGroupIcon className="nav-icon" />,
      path: '/customers'
    },
    {
      id: 'users',
      label: 'المستخدمين',
      icon: <UserGroupIcon className="nav-icon" />,
      path: '/users'
    },
    {
      id: 'settings',
      label: 'الإعدادات',
      icon: <Cog6ToothIcon className="nav-icon" />,
      path: '/settings'
    }
  ];

  const getActiveTab = () => {
    const path = location.pathname;
    const activeItem = navItems.find(item => item.path === path);
    return activeItem ? activeItem.id : 'home';
  };

  const handleTabClick = (path) => {
    navigate(path);
  };

  return (
    <nav className="navbar">
      {/* Logo/Brand Section */}
      <div className="nav-header">
        <div className="nav-logo">
          <BoltIcon className="logo-icon" />
          <span className="brand-name">Repario</span>
        </div>
      </div>

      {/* Navigation Items */}
      <div className="nav-container">
        {navItems.map((item) => (
          <button
            key={item.id}
            className={`nav-item ${getActiveTab() === item.id ? 'active' : ''}`}
            onClick={() => handleTabClick(item.path)}
            title={item.label}
          >
            <div className="nav-icon-container">
              {item.icon}
            </div>
            <div className="nav-label-container">
              <span className="nav-label">{item.label}</span>
            </div>
          </button>
        ))}
      </div>

      {/* Bottom Section */}
      <div className="nav-footer">
        {/* Language Selector */}
        <div className="language-selector">
          <GlobeAltIcon className="language-icon" />
          <span className="language-text">English</span>
        </div>

        {/* Theme Toggle */}
        <div className="theme-toggle">
          <div className="toggle-container">
            <span className="toggle-text">وضع النهار</span>
            <div className="toggle-switch">
              <div className="toggle-slider"></div>
            </div>
          </div>
        </div>

        {/* User Profile */}
        <div className="user-profile">
          <div className="user-avatar">
            <UserCircleIcon className="avatar-icon" />
          </div>
          <div className="user-info">
            <span className="user-name">حامد الحاجي</span>
            <span className="user-email">allreaps@gmail.com</span>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
