import React from 'react';
import './InvoiceCard.css';

const InvoiceCard = ({ title, amount, paid, client, invoiceNumber, dueDate, status }) => (
  <div className="invoice-card">
    <div className="invoice-title">{title}</div>
    <div className="invoice-details">
      <span className="invoice-amount">{amount} ر.س</span>
      <span className="invoice-paid">{paid} متبقي</span>
    </div>
    <div className="invoice-client">العميل: {client}</div>
    <div className="invoice-number">رقم الفاتورة: {invoiceNumber}</div>
    <div className="invoice-status-row">
      <span className={`invoice-status ${status}`}>{status === 'جزئي' ? 'جزئي' : status === 'معلقة' ? 'معلقة' : 'تحصيل'}</span>
      <span className="invoice-due">استحقاق: {dueDate}</span>
    </div>
  </div>
);

export default InvoiceCard;
