import React from 'react';
import FinancialCard from '../components/FinancialCard';
import StatusCard from '../components/StatusCard';
import InvoiceCard from '../components/InvoiceCard';
import TransactionCard from '../components/TransactionCard';
import ActionButton from '../components/ActionButton';
import SectionHeader from '../components/SectionHeader';
import './Dashboard.css';

const Dashboard = () => {
  // Sample data
  const statusData = [
    { amount: '3,250,000', label: 'ŸÖÿØŸÅŸàÿπ', type: 'success', icon: 'üí∞' },
    { amount: '1,250,000', label: 'ŸÖÿπŸÑŸÇ', type: 'warning', icon: '‚è∞' },
    { amount: '250,000', label: 'ŸÖÿ™ÿ£ÿÆÿ±', type: 'danger', icon: '‚ö†Ô∏è' }
  ];

  const invoices = [
    {
      title: 'ŸÖÿ¥ÿ±Ÿàÿπ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ™ÿ¨ÿßÿ±Ÿä',
      client: 'ÿßŸÑÿπŸÖŸäŸÑ: ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ',
      amount: '500,000',
      invoiceNumber: 'ÿ±ŸÇŸÖ ÿßŸÑŸÅÿßÿ™Ÿàÿ±ÿ©: INV-001',
      date: 'ÿßÿ≥ÿ™ÿ≠ŸÇÿßŸÇ: 25-01-2024',
      actions: [
        { label: 'ÿ™ÿ∞ŸÉŸäÿ±', type: 'primary' },
        { label: 'ÿ™ÿ≠ÿµŸäŸÑ', type: 'success' },
        { label: 'ÿ≠ÿ∞ŸÅ', type: 'danger' }
      ]
    },
    {
      title: 'ÿ™ÿµŸÖŸäŸÖ ÿ™ÿ∑ÿ®ŸäŸÇ ÿßŸÑŸÖÿ∑ÿπŸÖ',
      client: 'ÿßŸÑÿπŸÖŸäŸÑ: ŸÖÿ≠ŸÖÿØ ÿπŸÑŸä',
      amount: '350,000',
      invoiceNumber: 'ÿ±ŸÇŸÖ ÿßŸÑŸÅÿßÿ™Ÿàÿ±ÿ©: INV-002',
      date: 'ÿßÿ≥ÿ™ÿ≠ŸÇÿßŸÇ: 20-01-2024',
      actions: [
        { label: 'ÿ™ÿ∞ŸÉŸäÿ±', type: 'primary' },
        { label: 'ÿ™ÿ≠ÿµŸäŸÑ', type: 'success' },
        { label: 'ŸÖŸÑÿ∫Ÿäÿ©', type: 'danger' }
      ]
    }
  ];

  const transactions = [
    {
      title: 'ŸÖÿ¥ÿ±Ÿàÿπ ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿ≠ŸÑ ÿßŸÑÿ™ÿ¨ÿßÿ±Ÿä',
      amount: '250,000',
      type: 'income',
      date: '15-01-2024 ‚Ä¢ ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ',
      icon: 'üìä'
    },
    {
      title: 'ÿ∑ÿ®ÿßÿπÿ© ÿ®ÿ±Ÿàÿ¥Ÿàÿ±ÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©',
      amount: '80,000',
      type: 'expense',
      date: '14-01-2024 ‚Ä¢ ÿ≥ÿßÿ±ÿ© ÿ£ÿ≠ŸÖÿØ',
      icon: 'üñ®Ô∏è'
    },
    {
      title: 'ÿ¥ÿ±ÿßÿ° ÿßŸÑÿ£ŸÑŸÖŸÜŸäŸàŸÖ ŸàÿßŸÑÿ≤ÿ¨ÿßÿ¨',
      amount: '120,000',
      type: 'expense',
      date: '13-01-2024 ‚Ä¢ ŸÖŸàÿ±ÿØ ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑÿÆÿßŸÖ',
      icon: 'üèóÔ∏è'
    }
  ];

  const actionButtons = [
    { title: 'ÿ≥ŸÜÿØ ÿµÿ±ŸÅ ÿ¨ÿØŸäÿØ', icon: '‚Üë', color: 'red' },
    { title: 'ÿ≥ŸÜÿØ ŸÇÿ®ÿ∂ ÿ¨ÿØŸäÿØ', icon: '‚Üì', color: 'green' },
    { title: 'ÿ≥ŸÜÿØ ÿ™ÿ≠ŸàŸäŸÑ', icon: 'üí±', color: 'purple' },
    { title: 'ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿµÿ±ŸÅŸäÿßÿ™', icon: 'üìä', color: 'orange' }
  ];

  const monthlyExpenses = [
    { category: 'ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑÿÆÿßŸÖ', amount: '850,000', color: '#f44336' },
    { category: 'ÿßŸÑÿ±Ÿàÿßÿ™ÿ®', amount: '620,000', color: '#2196f3' },
    { category: 'ÿßŸÑŸÖÿ±ÿßŸÅŸÇ', amount: '210,000', color: '#ff9800' },
    { category: 'ÿ£ÿÆÿ±Ÿâ', amount: '120,000', color: '#4caf50' }
  ];

  return (
    <div className="dashboard">
      {/* Header */}
      <div className="dashboard-header">
        <div className="user-info">
          <div className="user-avatar">üë§</div>
          <div className="user-details">
            <h1>ŸÜŸàÿ±ÿß ÿ≥ÿßŸÑŸÖ</h1>
            <span>ŸÖÿ≠ÿßÿ≥ÿ®</span>
          </div>
        </div>
        <div className="header-actions">
          <button className="notification-btn">üîî</button>
          <button className="menu-btn">‚ò∞</button>
        </div>
      </div>

      {/* Financial Overview */}
      <FinancialCard 
        title="ÿßŸÑŸàÿ∂ÿπ ÿßŸÑŸÖÿßŸÑŸä - ŸäŸÜÿßŸäÿ± 2024"
        revenue="2,700,000"
        expenses="4,500,000"
        period="ŸäŸÜÿßŸäÿ± 2024"
      />

      {/* Status Cards */}
      <div className="status-grid">
        {statusData.map((status, index) => (
          <StatusCard 
            key={index}
            amount={status.amount}
            label={status.label}
            type={status.type}
            icon={status.icon}
          />
        ))}
      </div>

      {/* Pending Invoices */}
      <div className="section">
        <SectionHeader 
          title="ÿßŸÑŸÅŸàÿßÿ™Ÿäÿ± ÿßŸÑŸÖÿπŸÑŸÇÿ©" 
          actionText="ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ"
        />
        <div className="invoices-list">
          {invoices.map((invoice, index) => (
            <InvoiceCard 
              key={index}
              title={invoice.title}
              client={invoice.client}
              amount={invoice.amount}
              invoiceNumber={invoice.invoiceNumber}
              date={invoice.date}
              actions={invoice.actions}
            />
          ))}
        </div>
      </div>

      {/* Recent Transactions */}
