import React from 'react';
import './InvoiceCard.css';

const InvoiceCard = ({ 
  title, 
  client, 
  amount, 
  invoiceNumber, 
  date, 
  status = 'pending',
  actions = []
}) => {
  return (
    <div className="invoice-card">
      <div className="invoice-content">
        <div className="invoice-left">
          <div className="invoice-amount">
            <span className="amount">{amount}</span>
            <span className="currency">ج.س</span>
          </div>
          <div className="invoice-subtitle">
            <span className="remaining">متبقي: {amount}</span>
          </div>
        </div>

        <div className="invoice-right">
          <div className="invoice-info">
            <h4>{title}</h4>
            <p className="client-name">{client}</p>
            <p className="invoice-number">{invoiceNumber}</p>
            <p className="invoice-date">{date}</p>
          </div>
        </div>
      </div>

      <div className="invoice-actions">
        {actions.map((action, index) => (
          <button 
            key={index} 
            className={`action-btn ${action.type}`}
            onClick={action.onClick}
          >
            {action.label}
          </button>
        ))}
      </div>
    </div>
  );
};

export default InvoiceCard;
