import React from 'react';
import { FaBell, FaSearch, FaChevronDown, FaPlus, FaFileInvoiceDollar, FaClock, FaCalendarAlt } from 'react-icons/fa';
import { BsArrowDownCircle, BsArrowUpCircle, BsThreeDotsVertical } from 'react-icons/bs';
import { RiMoneyDollarCircleLine } from 'react-icons/ri';
import { HiOutlineUserCircle } from 'react-icons/hi';
import { IoMdArrowDropdown } from 'react-icons/io';
import './Dashboard.css';

// Components
import FinancialStatus from '../components/Dashboard/FinancialStatus';
import SummaryCard from '../components/Dashboard/SummaryCard';
import InvoiceList from '../components/Dashboard/InvoiceList';
import TransactionList from '../components/Dashboard/TransactionList';

const Dashboard = () => {
  // Sample data - in a real app, this would come from an API
  const summaryData = [
    { id: 1, title: 'Paid', amount: '$12,000', change: '+15%', trend: 'up' },
    { id: 2, title: 'Pending', amount: '$8,500', change: '+5%', trend: 'up' },
    { id: 3, title: 'Overdue', amount: '$2,500', change: '-2%', trend: 'down' }
  ];

  const invoices = [
    { id: 1, client: 'John Doe', amount: 1200, dueDate: '2023-10-15', status: 'pending' },
    { id: 2, client: 'Acme Inc', amount: 850, dueDate: '2023-10-10', status: 'paid' },
    { id: 3, client: 'Tech Corp', amount: 2450, dueDate: '2023-10-05', status: 'overdue' },
    { id: 4, client: 'Design Studio', amount: 500, dueDate: '2023-10-20', status: 'pending' },
  ];

  const transactions = [
    { id: 1, description: 'Website Redesign', client: 'John Doe', amount: 1200, date: '2023-10-01', type: 'income' },
    { id: 2, description: 'Hosting Service', client: 'Acme Inc', amount: -50, date: '2023-10-02', type: 'expense' },
    { id: 3, description: 'Logo Design', client: 'Tech Corp', amount: 800, date: '2023-10-03', type: 'income' },
  ];

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="dashboard-header">
        <div className="header-left">
          <h1>Dashboard</h1>
          <div className="date-range">
            <span>This Week</span>
            <IoMdArrowDropdown />
          </div>
        </div>
        <div className="header-right">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input type="text" placeholder="Search..." />
          </div>
          <button className="notification-btn">
            <FaBell />
            <span className="notification-badge">3</span>
          </button>
          <div className="user-profile">
            <HiOutlineUserCircle className="user-avatar" />
            <span>John Doe</span>
            <FaChevronDown className="dropdown-icon" />
          </div>
        </div>
      </header>

      <main className="dashboard-content">
        {/* Financial Status Card */}
        <FinancialStatus 
          totalRevenue="$24,780" 
          change="+12.5%" 
          isIncrease={true} 
          chartData={[30, 40, 45, 50, 49, 60, 70, 91, 125]}
        />

        {/* Summary Cards */}
        <div className="summary-cards">
          {summaryData.map(item => (
            <SummaryCard 
              key={item.id}
              title={item.title}
              amount={item.amount}
              change={item.change}
              isIncrease={item.trend === 'up'}
            />
          ))}
        </div>

        <div className="dashboard-grid">
          {/* Invoice List */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Pending Invoices</h3>
              <button className="btn-text">View All</button>
            </div>
            <InvoiceList invoices={invoices} />
          </div>

          {/* Recent Transactions */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Recent Transactions</h3>
              <button className="btn-text">View All</button>
            </div>
            <TransactionList transactions={transactions} />
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
