import React from 'react';
import FinancialCard from '../components/FinancialCard';
import StatusCard from '../components/StatusCard';
import InvoiceCard from '../components/InvoiceCard';
import TransactionCard from '../components/TransactionCard';
import ActionButton from '../components/ActionButton';
import SectionHeader from '../components/SectionHeader';
import { 
  CurrencyDollarIcon, 
  ClockIcon, 
  ExclamationTriangleIcon,
  ArrowUpIcon,
  ArrowDownIcon,
  ArrowsRightLeftIcon,
  ChartBarIcon,
  ClipboardDocumentListIcon,
  PrinterIcon,
  BuildingOfficeIcon
} from '@heroicons/react/24/outline';
import '../styles/Dashboard.css';

const Dashboard = () => {
  // Sample data
  const statusData = [
    { amount: '3,250,000', label: 'ŸÖÿØŸÅŸàÿπ', type: 'success', icon: <CurrencyDollarIcon className="w-6 h-6" /> },
    { amount: '1,250,000', label: 'ŸÖÿπŸÑŸÇ', type: 'warning', icon: <ClockIcon className="w-6 h-6" /> },
    { amount: '250,000', label: 'ŸÖÿ™ÿ£ÿÆÿ±', type: 'danger', icon: <ExclamationTriangleIcon className="w-6 h-6" /> }
  ];

  const invoices = [
    {
      title: 'ŸÖÿ¥ÿ±Ÿàÿπ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ™ÿ¨ÿßÿ±Ÿä',
      client: 'ÿßŸÑÿπŸÖŸäŸÑ: ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ',
      amount: '500,000', 
      AmountLeft: '250,000',
      invoiceNumber: 'ÿ±ŸÇŸÖ ÿßŸÑŸÅÿßÿ™Ÿàÿ±ÿ©: INV-001',
      date: 'ÿßÿ≥ÿ™ÿ≠ŸÇÿßŸÇ: 25-01-2024',
      status: 'partial',
      actions: [
        { label: 'ÿ™ÿ∞ŸÉŸäÿ±', type: 'primary' },
        { label: 'ÿ™ÿ≠ÿµŸäŸÑ', type: 'success' },
      ]
    },
    {
      title: 'ÿ™ÿµŸÖŸäŸÖ ÿ™ÿ∑ÿ®ŸäŸÇ ÿßŸÑŸÖÿ∑ÿπŸÖ',
      client: 'ÿßŸÑÿπŸÖŸäŸÑ: ŸÖÿ≠ŸÖÿØ ÿπŸÑŸä',
      amount: '350,000',
      AmountLeft: '250,000',
      invoiceNumber: 'ÿ±ŸÇŸÖ ÿßŸÑŸÅÿßÿ™Ÿàÿ±ÿ©: INV-002',
      date: 'ÿßÿ≥ÿ™ÿ≠ŸÇÿßŸÇ: 20-01-2024',
      status: 'pending',
      actions: [
        { label: 'ÿ™ÿ∞ŸÉŸäÿ±', type: 'primary' },
        { label: 'ÿ™ÿ≠ÿµŸäŸÑ', type: 'success' },
      ]
    }
  ];

  const transactions = [
    {
      title: 'ŸÖÿ¥ÿ±Ÿàÿπ ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿ≠ŸÑ ÿßŸÑÿ™ÿ¨ÿßÿ±Ÿä',
      amount: '250,000',
      type: 'income',
      date: '15-01-2024 ‚Ä¢ ÿ£ÿ≠ŸÖÿØ ŸÖÿ≠ŸÖÿØ',
      icon: <ChartBarIcon className="w-5 h-5" />
    },
    {
      title: 'ÿ∑ÿ®ÿßÿπÿ© ÿ®ÿ±Ÿàÿ¥Ÿàÿ±ÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿ©',
      amount: '80,000',
      type: 'expense',
      date: '14-01-2024 ‚Ä¢ ÿ≥ÿßÿ±ÿ© ÿ£ÿ≠ŸÖÿØ',
      icon: <PrinterIcon className="w-5 h-5" />
    },
    {
      title: 'ÿ¥ÿ±ÿßÿ° ÿßŸÑÿ£ŸÑŸÖŸÜŸäŸàŸÖ ŸàÿßŸÑÿ≤ÿ¨ÿßÿ¨',
      amount: '120,000',
      type: 'expense',
      date: '13-01-2024 ‚Ä¢ ŸÖŸàÿ±ÿØ ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑÿÆÿßŸÖ',
      icon: <BuildingOfficeIcon className="w-5 h-5" />
    }
  ];

  const actionButtons = [
    { title: 'ÿ≥ŸÜÿØ ÿµÿ±ŸÅ ÿ¨ÿØŸäÿØ', icon: '‚Üë', color: 'red' },
    { title: 'ÿ≥ŸÜÿØ ŸÇÿ®ÿ∂ ÿ¨ÿØŸäÿØ', icon: '‚Üì', color: 'green' },
    { title: 'ÿ≥ŸÜÿØ ÿ™ÿ≠ŸàŸäŸÑ', icon: '‚áÑ', color: 'purple' },
    { title: 'ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿµÿ±ŸÅŸäÿßÿ™', icon: 'üìã', color: 'orange' }
  ];

  const monthlyExpenses = [
    { category: 'ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑÿÆÿßŸÖ', amount: '850,000', color: '#f44336' },
    { category: 'ÿßŸÑÿ±Ÿàÿßÿ™ÿ®', amount: '620,000', color: '#2196f3' },
    { category: 'ÿßŸÑŸÖÿ±ÿßŸÅŸÇ', amount: '210,000', color: '#ff9800' },
    { category: 'ÿ£ÿÆÿ±Ÿâ', amount: '120,000', color: '#4caf50' }
  ];

  return (
    <div className="dashboard">

      {/* Financial Overview */}
      <FinancialCard 
        title="ÿßŸÑŸàÿ∂ÿπ ÿßŸÑŸÖÿßŸÑŸä - ŸäŸÜÿßŸäÿ± 2024"
        revenue="2,700,000"
        expenses="4,500,000"
      />

      {/* Status Cards */}
      <div className="status-grid">
        {statusData.map((status, index) => (
          <StatusCard 
            key={index}
            amount={status.amount}
            label={status.label}
            type={status.type}
            icon={status.icon}
          />
        ))}
      </div>

      {/* Pending Invoices */}
      <div className="section">
        <div className="invoices-list">
        <SectionHeader 
          title="ÿßŸÑŸÅŸàÿßÿ™Ÿäÿ± ÿßŸÑŸÖÿπŸÑŸÇÿ©" 
          actionText="ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ"
        />
          {invoices.map((invoice, index) => (
            <InvoiceCard 
              key={index}
              title={invoice.title}
              client={invoice.client}
              amount={invoice.amount}
              AmountLeft={invoice.AmountLeft}
              invoiceNumber={invoice.invoiceNumber}
              date={invoice.date}
              status={invoice.status}
              actions={invoice.actions}
            />
          ))}
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="section">
        <div className="transactions-list">
        <SectionHeader 
          title="ÿßŸÑŸÖÿπÿßŸÖŸÑÿßÿ™ ÿßŸÑÿ£ÿÆŸäÿ±ÿ©" 
          actionText="ÿπÿ±ÿ∂ ÿßŸÑŸÉŸÑ"
        />
          {transactions.map((transaction, index) => (
            <TransactionCard 
              key={index}
              title={transaction.title}
              amount={transaction.amount}
              type={transaction.type}
              date={transaction.date}
              icon={transaction.icon}
            />
          ))}
        </div>
      </div>

      {/* Action Buttons */}
      <div className="actions-grid">
        {actionButtons.map((action, index) => (
          <ActionButton 
            key={index}
            title={action.title}
            icon={action.icon}
            color={action.color}
          />
        ))}
      </div>

      {/* Project Management */}
      <div className="section">
