import React from 'react';
import FinancialStatusCard from '../components/FinancialStatusCard';
import StatusSummaryCard from '../components/StatusSummaryCard';
import InvoiceCard from '../components/InvoiceCard';
import TransactionCard from '../components/TransactionCard';
import ActionButtonCard from '../components/ActionButtonCard';
import ProjectManagementCard from '../components/ProjectManagementCard';
import MonthlyExpensesCard from '../components/MonthlyExpensesCard';
import './Dashboard.css';

const Dashboard = () => {
	const invoices = [
	  {
	    title: 'مشروع لافتة المحل التجاري',
	    amount: '500,000',
	    paid: '250,000',
	    client: 'أحمد محمد',
	    invoiceNumber: 'INV-001',
	    dueDate: '25-01-2024',
	    status: 'جزئي',
	  },
	  {
	    title: 'تصميم نيون المطعم',
	    amount: '350,000',
	    paid: '350,000',
	    client: 'محمد علي',
	    invoiceNumber: 'INV-002',
	    dueDate: '20-01-2024',
	    status: 'معلقة',
	  },
	];

	const transactions = [
	  {
	    title: 'مشروع لافتة المحل التجاري',
	    amount: '+250,000 د.ع',
	    type: 'income',
	    client: 'أحمد محمد',
	    date: '15-01-2024',
	  },
	  {
	    title: 'طباعة بروشورات الشركة',
	    amount: '+80,000 د.ع',
	    type: 'electronic',
	    client: 'سارة أحمد',
	    date: '14-01-2024',
	  },
	  {
	    title: 'شراء ألمنيوم وأكريليك',
	    amount: '-120,000 د.ع',
	    type: 'expense',
	    client: 'مورد المواد الخام',
	    date: '13-01-2024',
	  },
	];

	const expenses = [
	  { label: 'المواد الخام', amount: '850,000', color: 'red' },
	  { label: 'الرواتب', amount: '620,000', color: 'blue' },
	  { label: 'المرافق', amount: '210,000', color: 'yellow' },
	  { label: 'أخرى', amount: '120,000', color: 'green' },
	];

	return (
		<div className="dashboard-container">
			<div className="dashboard-header">
				<div>نورا سالم <span style={{background:'#ffe066',borderRadius:'50%',padding:'4px 8px',marginLeft:'8px'}}>محاسب</span></div>
			</div>
			<div className="dashboard-cards">
				<FinancialStatusCard totalRevenue="4,500,000" netProfit="2,700,000" />
				<StatusSummaryCard paid="3,250,000" pending="1,250,000" overdue="250,000" />
				<div className="dashboard-section">
					<h3 style={{textAlign:'right',margin:'12px 0'}}>الفواتير المعلقة</h3>
					{invoices.map((inv, idx) => <InvoiceCard key={idx} {...inv} />)}
				</div>
				<div className="dashboard-section">
					<h3 style={{textAlign:'right',margin:'12px 0'}}>المعاملات الأخيرة</h3>
					{transactions.map((tr, idx) => <TransactionCard key={idx} {...tr} />)}
				</div>
				<div className="dashboard-actions">
					<ActionButtonCard label="سند صرف جديد" color="red" />
					<ActionButtonCard label="سند قبض جديد" color="green" />
					<ActionButtonCard label="سند تحويل" color="purple" />
					<ActionButtonCard label="حساب المصرفيات" color="orange" />
				</div>
				<ProjectManagementCard />
				<MonthlyExpensesCard expenses={expenses} />
			</div>
		</div>
	);
};

export default Dashboard;
